import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { navigate } from '@reach/router';
import {Link} from '@reach/router';

const EditPet = (props) => {
    const [petDetails, setPetDetails] = useState({
        name:'',
        type:'',
        description:'',
        skillOne:'',
        skillTwo:'',
        skillThree:'',
    })

    const [formErrors, setFormErrors] = useState({})

    useEffect(()=>{
        axios
            .get(`http://localhost:8000/api/pet/${props._id}`)
            .then(res=>{
                console.log("Retrieving author...")
                console.log(res)
                console.log("Got it.")
                setPetDetails(res.data.pet)
            })
            .catch(err=>{
                console.log("Error: ", err)
            })
    }, [])

    const onChangeHandler = (event) => {
        console.log("Changes")
        setPetDetails({
            ...petDetails,
            [event.target.name]: event.target.value,
            [event.target.type]: event.target.value,
            [event.target.description]: event.target.value,
            [event.target.skillOne]: event.target.value,
            [event.target.skillTwo]: event.target.value,
            [event.target.skillThree]: event.target.value,

        })
    }

    const onSubmitHandler = (event) => {
        event.preventDefault()
        axios.put(`http://localhost:8000/api/updateExistingPet/${props._id}`, petDetails)
            .then(res=>{
                console.log("Response: ", res)
                if(res.data.error) {
                    console.log("Please fill form out properly.")
                    setFormErrors(res.data.error.errors) 
                }
                else {
                    navigate("/")

                }
            })
            .catch(err=>console.log("Error: ", err))

    }

    return (
        <div>
            <Link to={`/`}>Home</Link>
            <h2>Edit {petDetails.name}</h2>
            <form onSubmit={onSubmitHandler}>
            <div className="mb-3" >
                <input onChange={onChangeHandler} type="text" name="name" value={petDetails.name}/>
                {
                    formErrors.name ?
                        <p style={{color:'red'}}>{ formErrors.name.message }</p> :
                        ''
                }
            </div>
            <div className="mb-3" >
                <input onChange={onChangeHandler} type="text" name="type" value={petDetails.type}/>
                {
                    formErrors.type ?
                        <p style={{color:'red'}}>{ formErrors.type.message }</p> :
                        ''
                }
            </div>
            <div className="mb-3" >
                <input onChange={onChangeHandler} type="text" name="description" value={petDetails.description}/>
                {
                    formErrors.description ?
                        <p style={{color:'red'}}>{ formErrors.description.message }</p> :
                        ''
                }
            </div>
            <div className="mb-3" >
                <input onChange={onChangeHandler} type="text" name="skillOne" value={petDetails.skillOne}/>
            </div>
            <div className="mb-3" >
                <input onChange={onChangeHandler} type="text" name="skillTwo" value={petDetails.skillTwo}/>
            </div>
            <div className="mb-3" >
                <input onChange={onChangeHandler} type="text" name="skillThree" value={petDetails.skillThree}/>
            </div>
            <a className="btn btn-danger" href="/" >Cancel</a>
            <input type="submit" value = "Upload"/>
        </form>

        </div>
    );
};

export default EditPet;
